</template>

<script>


    import {Draggable} from 'gsap/Draggable'
    import BackDrone from '@/components/BackDrone'

    export default {
        name: 'choose-an-landscape',
        props: ['landscape'],

        components: {
            BackDrone
        },
        data() {
            return {
                show: false,
                backHome: false,
                droneBack: false,
                city: this.landscape,
                back: false
            }
        },
        sockets: {
            connect() {
            },
            disconnect() {
            },
            stopByUserDrone: function () {
                this.back = true

            }
            // On attend la connection du drone
        },

        methods: {
            backHomeDrone() {
                // Emit qui stop le drone en vol
                this.backHome = true
            },

            goHome(value) {
                console.log(value);
                if (value) {
                    this.backHome = false
                    this.droneBack = true
                    this.$emit("backHomeDrone", true)
                    // Emit la personne va rependre le controle + ecran de fin
                }
            }


        },
        mounted() {
        },
    }
</script>

<!-- Add "scoped" attribute to limit CSS to this component only -->
<style lang="scss">
    .choose-an-landscape {
        background: white;
        position: fixed;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        &--close {
            background: red;
            width: 20px;
            height: 20px;
            position: absolute;
            left: 0;
            top: 0;
        }
    }

    p {
        display: block;
    }

    .draggerBoundsCommand {
        position: absolute;
        left: 30px;
        top: 50%;
        -webkit-transform: translateY(-50%);
        -moz-transform: translateY(-50%);
        -ms-transform: translateY(-50%);
        -o-transform: translateY(-50%);
        transform: translateY(-50%);
        width: 50px;
        height: 250px;
        background: grey;
        margin: 20px;
        box-sizing: content-box;
    }

    .draggerCommand {
        width: 50px;
        height: 50px;
        background: red;
        text-align: center;
        line-height: 50px;
        position: absolute;
    }
    .fatigue {
        width: 100%;
        height: 100%;
        position: fixed;
        left: 0;
        top: 0;
        display: flex;
        align-items: center;
        justify-content: center;
        p {
            max-height: 400px;
            height: 100vh;
            display: flex;
            align-items: center;
            justify-content: center;
            background: rgba(255,255,255,0.4);
            width: 80%;

        }
        &:before {
            content: '';
            position: absolute;
            left: 0;
            top: 0;
            width: 100%;
            height: 100%;
            filter:blur(4px);
        }
    }
</style>
